import { useState,useEffect } from "react";
import {FaAngleLeft, FaAngleRight } from "react-icons/fa"



function Carousel({index,array}){

    const [currentIndex, setCurrentIndex] = useState(0)

    if(array[currentIndex] === array.length){
        setCurrentIndex(0)
    }

   return  (
       <div>
           <FaAngleRight  onClick={ ()=> setCurrentIndex(currentIndex + 1)}/>
           <FaAngleLeft  onClick={ ()=> setCurrentIndex(currentIndex - 1)}/>
         {index}{console.log(array[currentIndex])} 
         <img src={array[currentIndex]} alt="" />
       </div>
   ) 
}

export default Carousel;
