import { useState,useEffect } from "react";
import {FaAngleLeft, FaAngleRight } from "react-icons/fa"



function Carousel({index,array}){

    const [currentIndex, setCurrentIndex] = useState(0)
     console.log(currentIndex)
     const intinialState = {
         currentIndex: 0
     }


     if(currentIndex === array.lenght){
      return  setCurrentIndex(intinialState)
     }

   return  (
       <div>
           <FaAngleRight  onClick={ ()=> setCurrentIndex(currentIndex + 1)}/>
         {index}{console.log(array[currentIndex])} 
         <img src={array[currentIndex]} alt="" />
         <button onClick={() => setCurrentIndex(intinialState)}>reset</button>
       </div>
   )

}

export default Carousel;
