import { NavLink, useMatch } from "react-router-dom"

function CustomLink ({to,label}) {
  let resolved = useResolvedPath(to);
  let match = useMatch({ path: resolved.pathname, end: true });
return(

  <div>
  <Link
    style={{ textDecoration: match ? "underline" : "none" }}
    to={to}
    {...props}
  >
    {children}
  </Link>
  {match && " (active)"}
</div>
);
}

export {CustomLink};
