import {FaStar} from "react-icons/fa"
import { useState } from "react/cjs/react.development"


function Ranking ({rankingNumber}){

    const [isColored, setIsColored] = useState(false)
    const star = [1,2,3,4,5]

    const renderRanking = (number) => {
        if(rankingNumber >=  number){
            setIsColored(true)
            return ( <FaStar data-ranking={number} className="star"/>)
        }else{
             setIsColored(false)
            return ( <FaStar data-ranking={number} style={{color:"gray"}} className="star"/>)
        }


    }


    return (
        <div className="ranking_container">
         {star.map(number => {
            return renderRanking(number) 
         })}
        </div>
    )

}

export default Ranking;
